            $app12_fio_arr = explode('/', $item['app12_fio']);
            $app12_ids_arr = explode('/', $item['app12_ids']);
            $temp = $person = array();
            $nameNum = count($app12_ids_arr);
            for ($c = 0; $c < $nameNum; $c++) {
